package revision_java;

public class patter2n {

	public static void main(String[] args) {
	int n = 6;	
	int star=n;
		// TODO Auto-generated method stub
    for(int row=0;row<=n;row++) {
    	
    	for(int f=0 ;f<=star;f++) {
    		System.out.print("* ");
    		
    	}
    	System.out.println();
    	star--;
    	
    }
	}

}
